fetch("./js/data.json").then(t=>{if(!t.ok)throw Error("Error al cargar el archivo JSON: "+t.statusText);return t.json()}).then(t=>{let a=document.querySelector(".platosDiv"),e=document.querySelector(".content-img-platos");t.forEach(t=>{let i=document.createRange().createContextualFragment(`
            <div class="${t.clase}">
              <button type="button" data-id="${t.id}" onclick="filtroImgPlato(${t.id})">${t.nombrePlato}</button>
            </div>
            `);if(a.append(i),1==t.id){let o=document.querySelector('button[data-id="1"]'),l=document.createRange().createContextualFragment(`
                        <div class="plato-img">
                            <img src="${t.img1}" width="" height="" loading="lazy" alt="plato-${t.id}" />
                        </div>
                        <div class="plato-img">
                            <img src="${t.img2}" width="" height="" loading="lazy" alt="plato-${t.id}" />
                        </div>
                        <div class="plato-img">
                            <img src="${t.img3}" width="" height="" loading="lazy" alt="plato-${t.id}" />
                        </div>
                        `);e.append(l),o.classList.add("activeBtn")}})}).catch(t=>{console.error("Hubo un problema con la operaci\xf3n fetch:",t)});function filtroImgPlato(t){fetch("./js/data.json").then(t=>{if(!t.ok)throw Error("Error al cargar el archivo JSON: "+t.statusText);return t.json()}).then(a=>{let e=document.querySelector(".content-img-platos");a.forEach(a=>{let i=document.querySelector(`button[data-id="${a.id}"]`);if(i.classList.remove("activeBtn"),a.id==t){e.innerHTML="";let o=document.createRange().createContextualFragment(`
                        <div class="plato-img">
                            <img src="${a.img1}">
                        </div>
                        <div class="plato-img">
                            <img src="${a.img2}">
                        </div>
                        <div class="plato-img">
                            <img src="${a.img3}">
                        </div>
                        `);e.append(o),i.classList.add("activeBtn")}})}).catch(t=>{console.error("Hubo un problema con la operaci\xf3n fetch:",t)})}